import React from "react";
import "./QuestionBank.css";

const QuesBank = (props) => {
    const handleShowPopup = () => {
        props.popup(props);
    }
    const [isExpanded, setExpanded] = useState(false);

    const handleExpandClick = () => {
        setExpanded(!isExpanded); // Toggle the 'isExpanded' state
    };
    return (
        <>
        <div className="card">
            <div className="card-body">
                <h5 className="card-title">Question {props.id}</h5>
                <p className="card-text">{props.ques}</p>
                {isExpanded && (
                    <div>
                    {/* Content to expand */}
                    <p>This is the expanded content.</p>
                    </div>
                )}
                <button className="btn btn-primary" onClick={handleShowPopup}>Read</button>
            </div>
        </div>
        </>
    )
}

export default QuesBank;
