import React, { useState } from "react";


const [formData, setFormData] = useState({
    companyName: '',
    question: '',
    answer: ''
})

const addQues = () => {    

    const HandleChange = (event) => {
        const [name, value] = event.target;
        setFormData[{...HandleChange, [name] : value}]
        alert(formData);
    }
    // const HandleSubmit = () => {       

    // }
    return(
        <>
            <form onSubmit={HandleSubmit}>
                <div class="form-group">
                    <label for="companyName">Company name</label>
                    <input type="text" class="form-control" id="companyName" name='companyName' value={formData.companyName} onChange= {HandleChange} placeholder="Rovers Compass"/>
                </div><div class="form-group">
                <label for="questions">Questions</label>
                    <input type="text" class="form-control" id="questions" name='question' value={formData.question} onChange= {HandleChange} placeholder="Whats your name?"/>
                </div>
                <div class="form-group">
                    <label for="answers">Answer</label>
                    <textarea class="form-control" id="answers" name='answer' value={formData.answer} onChange= {HandleChange} rows="3"></textarea>
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </>
    );
}

export default addQues;
