import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import QuesBank from "./QuesBank";
import { useState, useEffect } from 'react';
import PopupForm from './PopupForm';
function App() {

  const [isPopupVisible, SetPopUpVisible] = useState(false);
  const handleShowPopup = () => {
    SetPopUpVisible(true);
  }
  const handleClosePopup = () => {
    SetPopUpVisible(false);
  }

  const expandQuestion = (props) => {
    console.log(props.id);
  }

  const [data, setData] = useState([]);
  useEffect(() => {
      fetch('https://localhost:7145/api/QNAs', {
        method: 'GET',
      })
        .then((response) => response.json())
        .then((responseData) => {
          setData(responseData)
        })
        .catch( (error) => {
          console.error("Unable to get the data from backend. Because, ", error);
        }          
        )
    }, []
  );

  return (
    <>
      <nav className="navbar navbar-expand-lg navbar-light bg-light">
        <a className="navbar-brand" href="#">Interview Question-Answer Bank</a>
        <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <ul className="navbar-nav mr-auto">
            <li className="nav-item active">
              <a className="nav-link" href="#">Dashboard</a>
            </li>
            <li className="nav-item">
              <a className="nav-link" href="#">Add Questions</a>
            </li>
          </ul>
        </div>
      </nav>
      {console.log(data)}
      <div className='QuesCard'>
        {
          data.map((items) =>(
            <>
              <div className = "SingleCard">
                <QuesBank id={items.id} ques={items.question} ans={items.answer} comp={items.company} popup = {expandQuestion}/>
              </div>
            </>
          ))
        }        
      </div>
      <div className="App">
        <button onClick={handleShowPopup} />
        {isPopupVisible && <PopupForm onClose={handleClosePopup} />}
      </div>
    </>
  );
}

export default App;
