    }
    return(
        <>
            <form onSubmit={HandleSubmit}>
                <div class="form-group">
                    <label for="companyName">Company name</label>
                    <input type="text" class="form-control" id="companyName" value={formData.companyName} placeholder="Rovers Compass"/>
                </div><div class="form-group">
                <label for="questions">Questions</label>
                    <input type="text" class="form-control" id="questions" value={formData.question} placeholder="Whats your name?"/>
                </div>
                <div class="form-group">
                    <label for="answers">Answer</label>
                    <textarea class="form-control" id="answers" value={formData.answer} rows="3"></textarea>
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </>
    );
}

export default addQues;
